import {useState} from 'react'

import Header from './components/Header'
import Tasks from './components/Tasks'
import AddTask from './components/AddTask'

const App = () => {

    const [showAddForm, setShowAddForm] = useState(false)

    const [tasks , setTasks] = useState([
        {
        id: 1,
        text: 'Assignent Submittion',
        day: 'feb 4th at 3:30pm',
        reminder : true,
    },
    {
        id: 2,
        text: 'Docter Appoinment',
        day :'feb 5th at 1:30pm',
        reminder : false,
    },
    {
        id: 3,
        text: 'Shoping',
        day: 'feb 4th at 6:30pm',
        reminder : true,
    },
    ])

    //Add Task
    const addTask = (task) =>{
        const id = Math.floor(Math.random()*100) +1
        const newTask = {id, ...task}
        setTasks([...tasks, newTask])
    }

    //Delete task
    const deleteTasks = (id) =>{
        console.log('deleting' ,id)
        setTasks(tasks.filter((task)=> task.id !== id))
    }

    //togel reminder

    const toggleReminder = (id) =>{
        console.log('toggleReminder', id)
        setTasks(tasks.map((task)=> task.id === id ? {
            ...task, reminder: !task.reminder
        } : task))
    }


    return (
        <div className='container'>
            <Header onAdd={()=>setShowAddForm(!showAddForm) } showAdd={showAddForm}/>
            { showAddForm && <AddTask onAdd= {addTask}/>}
            {tasks.length>0 ?(
            <Tasks tasks={tasks} onDelete={deleteTasks} 
                onChange={toggleReminder}/>)
            : ('No Task Found!')}
        </div>
    )
}

export default App
