import Button from "./Button"

const Header = ({title, onAdd, showAdd}) => {

    return (
        <header className='header'>
            <h1>{title}</h1>
            <Button buttonText={showAdd ? 'Close' : 'Add'} 
                color={showAdd ? 'Close' : 'green'} onClick={onAdd}/>

        </header>
    )
}

Header.defaultProps = {
    title: 'Task Tracker'
}

export default Header
