import {useState} from 'react'

const Tasks = () => {

    const [tasks , setTasks] = useState([
        {
        id: 1,
        text: 'Assignent Submittion',
        day: 'feb 4th at 3:30pm',
        reminder : true,
    },
    {
        id: 2,
        text: 'Docter Appoinment',
        day :'feb 5th at 1:30pm',
        reminder : false,
    },
    {
        id: 3,
        text: 'Shoping',
        day: 'feb 4th at 6:30pm',
        reminder : true,
    },
    ])

    return (
        <>
        {tasks.map((task) =>(
        <h3 key={task.id}>{task.text}</h3>
        ))}

        </>
    )
}

export default Tasks
